import { useEffect, useState } from "react";
import LeaderBoard from "../LeaderBoard/LeaderBoard";
import Display from "../Display/Display";
import { AppDiv, Title } from "./App.styles";
import { UserInfo } from "../../types";
import { user_data } from "../../user_data";

const App = () => {
  const [userData, setUserData] = useState<UserInfo[]>([]);
  const [yourTime, setYourTime] = useState<number>(6039990)

  useEffect(()=>{
    const setInitialData = async () => {
      await setUserData(user_data);
      await setYourTime(userData[0].msTime);
    }

    setInitialData();

  }, [])

  useEffect(()=>{
    if(userData.length > 0) {
      let newUserData = userData;
      newUserData[0].msTime = yourTime;
      setUserData(newUserData);
    }
  }, [yourTime])

  return (
    <AppDiv>
      <Title>ðŸ¤¯ Memory Matching Game</Title>
      <Display userData={userData} setYourTime={setYourTime}/>
    </AppDiv>
  );
};

export default App;
